using System;

namespace BlackjackSimulation
{
    class B10MoveStrategy : IPlayerMoveStrategy
    {
        private int[] cardAmounts;
        private int tensAmount;
        private int _dealerUpCardValue, _playerTotalHard, _playerTotalSoft;
        private double chanceWWS_below18, chanceWWS_18, chanceWWS_19, chanceWWS_20, chanceWWS_21; // WWS = Win When Stand
        private bool _hasSplittedHand;
              

        public B10MoveStrategy(int[] cardAmounts)
        {
            this.cardAmounts = cardAmounts;
        }

        public MoveAction Response(Hand hand, Card dealerUpCard, bool hasSplittedHand)
        {
            Setup(hand, dealerUpCard, hasSplittedHand);

            
            bool hasSoftHand = false;

            for (int i = 0; i < hand.Cards.Count; i++)
            {
                if (hand.GetValues().Length == 2)
                    hasSoftHand = true; 
            }

            //TODO Soft hand comparations..
            if (hand.Cards.Count == 2 && !hasSplittedHand && hand.Cards[0].GetCardValue() == hand.Cards[1].GetCardValue())
            {
                int cardValue = hand.Cards[0].GetCardValue();
                if (cardValue == 10 || cardValue == 9)
                    return MoveAction.Stand;
                else if (cardValue == 5)
                    return MoveAction.Double;
                else
                    return MoveAction.Split;

            }
            else if (hasSoftHand)
            {
                //Console.WriteLine("HardHit:{0}\tSoftHit:{1}\t\tHardStand:{2}\tSoftStand:{3}", WinChanceWhenHit(_playerTotalHard,false), WinChanceWhenHit(_playerTotalSoft,false), WinChanceWhenStand(_playerTotalHard), WinChanceWhenStand(_playerTotalSoft));
                if(_playerTotalSoft < 18)
                {
                    return MoveAction.Hit;
                }
                else
                {
                    return MoveAction.Stand;
                }
                
            }
            else
            {
                double winChanceStand = Math.Round(WinChanceWhenStand(_playerTotalHard), 4);
                double winChanceHit = Math.Round(WinChanceWhenHit(_playerTotalHard, false), 4);
                double winChanceDouble = Math.Round(WinChanceWhenDoubleDown(_playerTotalHard), 4);
                Console.ForegroundColor = ConsoleColor.Cyan;
                Console.WriteLine("Hit:{0}\tStand:{1}\t\tDoubleDown:{2}", winChanceHit, winChanceStand, winChanceDouble);
                Console.ForegroundColor = ConsoleColor.White;
                if (winChanceStand > winChanceHit)
                {
                    return MoveAction.Stand;
                }
                else
                {
                    if (winChanceDouble > 0.4)
                    {
                        //Console.ReadLine();
                        return MoveAction.Double;
                    }
                    else
                        return MoveAction.Hit;
                }
            }
        }

        private void Setup(Hand hand, Card dealerUpCard, bool hasSplittedHand)
        {
            tensAmount = cardAmounts[10] + cardAmounts[11] + cardAmounts[12] + cardAmounts[13];
            _hasSplittedHand = hasSplittedHand;
            _playerTotalHard = hand.GetValues()[0];
            if (hand.GetValues().Length > 1)
                _playerTotalSoft = hand.GetValues()[1];
            else
                _playerTotalSoft = 0;
            _dealerUpCardValue = dealerUpCard.GetCardValue();

            CalculateWWS();
        }


        private void CalculateWWS()
        {
            checked
            {
                try
                {
                    double dealerProbOfGoal17 = DealerProbOfGoal(17);
                    double dealerProbOfGoal18 = DealerProbOfGoal(18);
                    double dealerProbOfGoal19 = DealerProbOfGoal(19);
                    double dealerProbOfGoal20 = DealerProbOfGoal(20);
                    double dealerProbOfGoal21 = DealerProbOfGoal(21);

                    chanceWWS_below18 = 1d - (dealerProbOfGoal17 + dealerProbOfGoal18 + dealerProbOfGoal19 + dealerProbOfGoal20 + dealerProbOfGoal21);
                    chanceWWS_18 = 1d - (dealerProbOfGoal18 + dealerProbOfGoal19 + dealerProbOfGoal20 + dealerProbOfGoal21);
                    chanceWWS_19 = 1d - (dealerProbOfGoal19 + dealerProbOfGoal20 + dealerProbOfGoal21);
                    chanceWWS_20 = 1d - (dealerProbOfGoal20 + dealerProbOfGoal21);
                    chanceWWS_21 = 1d - dealerProbOfGoal21;
                }
                catch (OverflowException)
                {
                    throw;
                }
                catch (Exception)
                {
                    throw;
                }
            }
        }

        private double WinChanceWhenHit(int playerTotal, bool hasStandChance)
        {
            checked
            {
                try
                {
                    double probability = 0;

                    for (int cardValue = 1; cardValue < 12; cardValue++)
                        if (playerTotal + cardValue < 22)
                            probability += ProbOf(cardValue) * (double) WinChanceWhenHit(playerTotal + cardValue, true);

                    if (hasStandChance == true)
                        probability += WinChanceWhenStand(playerTotal);

                    return probability;
                }
                catch (OverflowException)
                {
                    throw;
                }
                catch (Exception)
                {
                    throw;
                }
            }
        }

        private double ProbOf(int cardIndex)
        {
            return (double) cardAmounts[cardIndex] / cardAmounts[0];
        }

        private double WinChanceWhenStand(int playerTotal)
        {
            if (playerTotal < 18)
                return chanceWWS_below18;
            else if (playerTotal == 18)
                return chanceWWS_18;
            else if (playerTotal == 19)
                return chanceWWS_19;
            else if (playerTotal == 20)
                return chanceWWS_20;
            else if (playerTotal == 21)
                return chanceWWS_21;
            else
                throw new ArgumentException("Player total > 21 !");
        }

        private double WinChanceWhenDoubleDown(int playerTotal)
        {
            double probability = 0;

            for (int cardValue = 1; cardValue < 12; cardValue++)
                if (playerTotal + cardValue < 22)
                {
                    probability += ProbOf(cardValue) * (double) WinChanceWhenStand(playerTotal + cardValue);
                    
                }
                    
            return probability;
        }
        


